var projectControllers=angular.module("projectControllers",["ngAnimate"]);projectControllers.controller("ListController",["$scope","$http",function(o,t){t.get("http://localhost:1337/projects/").success(function(e){o.projects=e,o.addProject=function(){var e={name:o.newProject,einnahmen:0,ausgaben:0};t.post("http://localhost:1337/projects",e).success(function(e,n,c){t.get("http://localhost:1337/projects/").success(function(t){o.projects=t}),o.newProject=""}).error(function(o){console.log(o)})}})}]),projectControllers.controller("DetailsController",["$scope","$http","$routeParams",function(o,t,e){t.get("http://localhost:1337/projects/"+e.itemId).success(function(n){o.projects=n,o.itemId=e.itemId,o.addEinnahme=function(){var e={label:o.labelNewEinnahme,type:"INCOME",value:1*o.amountNewEinnahme,project:o.itemId};t.post("http://localhost:1337/accountings",e).success(function(t){o.projects=t,o.labelNewEinnahme="",o.amountNewEinnahme=""}).error(function(o){console.log(o)})},o.addAusgabe=function(){var e={ausgaben:1*o.projects.ausgaben+1*o.amountNewAusgabe};t.post("http://localhost:1337/projects/"+o.itemId,e).success(function(t){o.projects=t,o.amountNewAusgabe=""}).error(function(o){console.log(o)})}})}]);